    public function getSourceContext()
    {
        return new Source("{% extends \"base.html.twig\" %}

{% block title %}
    Fridge App : Connection
{% endblock %}

{% block body %}
    <div class=\"text-center\">
        <h1>Fridge App</h1>
        <br>
        <div class=\"row h-100\">
            <div class=\"col-md-12 my-auto\">

                {% if error %}
                    <div class=\"alert alert-danger\">
                        {{ error.messageKey }}
                    </div>
                {% endif %}

                <form method=\"post\" name=\"form_login\" class=\"form-signin\" action=\"{{ path('login') }}\">
                    <h1 class=\"h3 mb-3 font-weight-normal\">Please sign in</h1>
                    <label for=\"inputEmail\" class=\"sr-only\">Email address</label>
                    <input type=\"text\" id=\"text\" name=\"_username\" class=\"form-control\" placeholder=\"Pseudo\" value=\"{{ last_username }}\" required
                           autofocus><br>
                    <label for=\"inputPassword\" class=\"sr-only\">Password</label>
                    <input type=\"password\" id=\"password\" name=\"_password\" class=\"form-control\" placeholder=\"Password\" required><br>
                    <button class=\"btn btn-lg btn-primary btn-block\" type=\"submit\">Sign in</button>
                </form>
            </div>
        </div>
    </div>
{% endblock %}
", "security/login.html.twig", "/var/www/html/Fridge_App/templates/security/login.html.twig");
